import React from "react";
import { useParams } from "react-router";
import { useWishCartContext } from "../../context/WishCartContext";
import productDetail from "./productDetail.module.css";

export const ProductDetail = () => {
  const { id } = useParams();
  //   console.log(id);
  const {
    state: { products },
  } = useWishCartContext();
  const {
    name,
    price,
    images: { img_1, img_2 },
    inStock,
    intro,
    summary,
    description,
    category,
    additionalInfo,
    fastDelivery,
  } = products?.find((item) => item.id === Number(id));
  return (
    <>
      <main>
        <div className={productDetail.container}>
          <div className={productDetail.flex}>
            <div className={productDetail.leftSide}>
              <figure>
                <img className={productDetail.img} src={img_1} alt="" />
                <img className={productDetail.subImg} src={img_1} alt="" />
                <img className={productDetail.subImg} src={img_2} alt="" />
              </figure>
            </div>
            <div className={productDetail.rightSide}>
              <h1>{name}</h1>
            </div>
          </div>
        </div>
      </main>
    </>
  );
};
