import React from "react";
import { useParams } from "react-router";
import { useWishCartContext } from "../../context/WishCartContext";
import { ShowOrHideAddToCart } from "../product/ShowOrHideAddToCart";
import { ShowOrHideWishIcon } from "../product/ShowOrHideWishIcon";
import productDetail from "./productDetail.module.css";

export const ProductDetail = () => {
  const { id } = useParams();
  //   console.log(id);
  const {
    state: { products },
  } = useWishCartContext();
  const {
    name,
    price,
    images: { img_1, img_2 },
    inStock,
    intro,
    summary,
    description,
    category,
    additionalInfo,
    fastDelivery,
  } = products?.find((item) => item.id === Number(id));
  return (
    <>
      <main className={productDetail.main}>
        <div className={productDetail.container}>
          <div className={productDetail.flex}>
            <div className={productDetail.leftSide}>
              <figure>
                <img className={productDetail.img} src={img_1} alt="" />
                <div style={{ display: "flex", justifyContent: "center" }}>
                  <img
                    className={`${productDetail.subImg} ${productDetail.subImg_1}`}
                    src={img_1}
                    alt=""
                  />
                  <img
                    className={`${productDetail.subImg} ${productDetail.subImg_2}`}
                    src={img_2}
                    alt=""
                  />
                </div>
              </figure>
            </div>
            <div className={productDetail.rightSide}>
              <div>
                <h1 style={{ fontWeight: "420" }}> {name}</h1>
                <h2 style={{ fontWeight: "480", color: "rgb(220,152,20)" }}>
                  â‚¹ {price}
                </h2>{" "}
                <ShowOrHideWishIcon
                  id={Number(id)}
                  iconPosition={productDetail.icon}
                />
              </div>
              <p
                style={{
                  textAlign: "justify",
                  width: "280px",
                  marginTop: "0.5rem",
                  fontSize: "1rem",
                  fontWeight: "450",
                  color: "#333",
                }}
              >
                {intro}
              </p>
              <ShowOrHideAddToCart id={Number(id)} btn={productDetail.btn} />
            </div>
          </div>
        </div>
      </main>
    </>
  );
};
