import React, { useState } from "react";
import { carouselItems } from "../../carouselDB";
import homeStyle from "./home.module.css";

export const Carousel = () => {
  return (
    <div className={homeStyle.main}>
      {carouselItems?.map(({ id, name, image }) => {
        const [navId, setNavId] = useState(id);
        return (
          <div className={homeStyle.carousel} key={id}>
            {id === navId && (
              <img
                src={image}
                alt=""
                style={{ width: "100%", height: "95vh" }}
              />
            )}
            {id === navId && (
              <div
                className={`${id === navId ? `${homeStyle.navLeft}` : " "} `}
                onCLick={() =>
                  setNavId((prev) => (prev === 1 ? prev : prev - 1))
                }
              >
                {" "}
                &#10094;
              </div>
            )}
            {id === navId && (
              <div
                className={`${id === navId ? `${homeStyle.navRight}` : " "} `}
                onCLick={() =>
                  setNavId((prev) => (prev === 3 ? prev : prev + 1))
                }
              >
                {" "}
                &#10095;
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
};
