import {
  createContext,
  useReducer,
  useContext,
  useEffect,
  useState,
} from "react";
import { products } from "../productsDB";
import { reducerFunc } from "../utilities";

const WishCartContext = createContext();

export const WishCartProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducerFunc, {
    cart: [],
    products,
    wishlist: [],
  });
  useEffect(
    () => localStorage.setItem("myCart", JSON.stringify(state.cart)),
    [state.cart]
  );
  useEffect(() => {
    const upCart = JSON.parse(localStorage.getItem("myCart"));
    console.log("UseEffect", JSON.parse(localStorage.getItem("myCart")));
    dispatch({ type: "CART-UPDATED", payload: upCart });
  }, []);
  return (
    <WishCartContext.Provider value={{ state, dispatch }}>
      {children}
    </WishCartContext.Provider>
  );
};

export const useWishCartContext = () => useContext(WishCartContext);
