import React from "react";
import productStyle from "./product.module.css";

export const FilterProducts = ({
  showFastDeliveryOnly,
  showInventoryAll,
  sortBy,
  dispatch,
  display,
}) => {
  return (
    <div
      className={productStyle.filterCard}
      style={{ display: `${display ? `block` : `none`}`, position: "relative" }}
    >
      <span style={{ position: "absolute", top: 0 }}>&times;</span>
      <h2>Sort By: </h2>
      <label className={productStyle.label}>
        <input
          type="radio"
          name="sort"
          onChange={() =>
            dispatch({ type: "SORT", payload: "PRICE_HIGH_TO_LOW" })
          }
          checked={sortBy && sortBy === "PRICE_HIGH_TO_LOW"}
          className={productStyle.input}
        ></input>
        Price - High to Low
      </label>
      <label>
        <input
          type="radio"
          name="sort"
          onChange={() =>
            dispatch({ type: "SORT", payload: "PRICE_LOW_TO_HIGH" })
          }
          checked={sortBy && sortBy === "PRICE_LOW_TO_HIGH"}
          className={productStyle.input}
        ></input>
        Price - Low to High
      </label>

      <h2>Filters: </h2>
      <label>
        <input
          type="checkbox"
          checked={showInventoryAll}
          onChange={() => dispatch({ type: "TOGGLE_INVENTORY" })}
          className={productStyle.input}
        />
        Include Out of Stock
      </label>

      <label className={productStyle.label}>
        <input
          type="checkbox"
          checked={showFastDeliveryOnly}
          onChange={() => dispatch({ type: "TOGGLE_DELIVERY" })}
          className={productStyle.input}
        />
        Fast Delivery Only
      </label>
      <label style={{ display: "block", marginTop: "1rem" }}>
        <h2> Price Range: </h2>
        <input type="range" className={productStyle.inputRange} />
      </label>
    </div>
  );
};
